$(document).on('submit', '#forgot-frm', function(event) {
	event.preventDefault();
	/* Act on the event */
	var resetbtn = $('#resetbtn');
	$('#email_empty').hide();
	$('#reset_succesmsg').text('').slideUp();
	$('#reset_msg').text('').slideUp();
	var email = $('#user_email').val();
	if(email == '' || !isValidEmail(email))
	{
		$('#email_empty').show();
	}else{
		$(resetbtn).prop('disabled', true);
		$(resetbtn).find('.fa-refresh').show();
		$.ajax({
			url: site_url+'register/reset_password',
			type: 'POST',
			dataType: 'json',
			data: {email: email},
			success: function (data) {
				$(resetbtn).prop('disabled', false);
				$(resetbtn).find('.fa-refresh').hide();
				if(data['error'] == 0)
				{
					$('#email_empty').show();
				}else if(data['error'] == 1)
				{
					$('#reset_succesmsg').text('Reset email sent successfully!').slideDown();
					setTimeout(function(){$('#reset_succesmsg').text('').slideUp();},3000);
				}else if(data['error'] == 2){
					$('#reset_msg').text('Email not found!').slideDown();
					setTimeout(function(){$('#reset_msg').text('').slideUp();},3000);
				}else{
					$('#reset_msg').text('Sorry! Please try again later.').slideDown();
					setTimeout(function(){$('#reset_msg').text('').slideUp();},3000);
				}
			}
		})
		.fail(function() {
			$(resetbtn).prop('disabled', false);
			$(resetbtn).find('.fa-refresh').hide();
			$('#reset_msg').text('Sorry! Please try again later.').slideDown();
			setTimeout(function(){$('#reset_msg').text('').slideUp();},3000);
			console.log("error");
		});
		
	}
});

