$(document).ready(function(){
	var stats = $('.state').val();
	var location = $('.location').val();
	var building = $('.building').val();
	
	$('.location_suggest').each(function () {
    	var ms = $(this).magicSuggest({
			allowFreeEntries: false,
			expandOnFocus: false,
			hideTrigger: true,
			typeDelay: 0,
			
			///selectionPosition: 'bottom',
			selectFirst: true,
			dataUrlParams: {state:stats,location:location,building:building},
			maxSuggestions: 20,
			queryParam: 'q',
			data: site_url+'ajax/locations'
		});
		$(ms).on('beforeload', function(event, combo) {
	  		var stats = $('.state').val();
	  		var location = $('.location').val();
	  		var building = $('.building').val();
			ms.setDataUrlParams({state:stats,location:location,building:building});
	  	});
	    $(ms).on('selectionchange', function(event, combo, records) {
			
			var obj = JSON.stringify(records);
			var obg = jQuery.parseJSON(obj);
			$('.state').val('');
			$('.location').val('');
			$('.building').val('');
			var state = '';
			var area = '';
			var building = '';
			$.each( obg, function( key, valu ) {
				if(valu.locations != '' )
				{	
					var searc_state = valu.state;
					
					var obj1 = JSON.stringify(records);
					var obg1 = jQuery.parseJSON(obj1);
					var i = 0;
					$.each(obg1, function(index, el) {
						
						if(searc_state == el.id && searc_state != el.locations  )
						{
							//alert();
							ms.removeFromSelection(records[i], true);
							//alert(JSON.stringify(records[i]))
							//records = records.splice($.inArray(i, records),1);
							//alert(JSON.stringify(records));
						}
						i++;
					});

					var searc_locat = valu.parent_location;
					var obj1 = JSON.stringify(records);
					var obg1 = jQuery.parseJSON(obj1);
					var i = 0;
					$.each(obg1, function(index, el) {
						
						if( searc_locat == el.id  )
						{
							//alert();
							ms.removeFromSelection(records[i], true);
							//alert(JSON.stringify(records[i]))
							//records = records.splice($.inArray(i, records),1);
							//alert(JSON.stringify(records));
						}
						i++;
					});
				}

				if(valu.building != '' )
				{	
					var searc_location = valu.locations;
					var obj1 = JSON.stringify(records);
					var obg1 = jQuery.parseJSON(obj1);
					var i = 0;
					$.each(obg1, function(index, el) {
						
						if(searc_location == el.id && (searc_location != el.building || searc_location != el.state) )
						{
							//alert();
							ms.removeFromSelection(records[i], true);
							//alert(JSON.stringify(records[i]))
							//records = records.splice($.inArray(i, records),1);
							//alert(JSON.stringify(records));
						}
						i++;
					});
				}
				//alert();
				//$('.location').val(valu.id);
			});
			var obj = JSON.stringify(records);
			var obg = jQuery.parseJSON(obj);
			$.each( obg, function( key, valu ) {
				if(valu.building != '' )
				{
					building += btoa(valu.building)+',';
				}else if(valu.locations != '' )
				{	
					area += btoa(valu.locations)+',';
				}else if(valu.state != '' )
				{
					state += btoa(valu.state)+',';
				}
				
				
				
			});

			$('.state').val(state);
			$('.location').val(area);
			$('.building').val(building);
		});
	    $('.reset').click(function(event) {
		/* Act on the event */
			$('#search_form').find('select option').removeAttr("selected");
			$('#search_form')[0].reset();
			ms.clear();
			
		});
	});
	
});

