$('#offprojectemail').click(function(event) {
    var thiss = $(this);

    $('.robot-error').text('');
    $('.lead_fail').slideUp();
	$('.lead_success').slideUp();
	$('.lead_dublicate').slideUp();
    var p_id = $('#pprop').val();
    var name = $('#pname').val();
    var email = $('#pemail').val();
    var phone = $('#purphone').val();
    var message = $('#pmessage').val();
    var robot = $('#probot:checked').val();
    var valid = true;
    if($.trim(name) == '')
    { $('#pname').attr('style', 'border-color:red !important'); valid=false;  }else{ $('#pname').attr('style', ''); }
    if(!isValidEmail(email))
    { $('#pemail').attr('style', 'border-color:red !important'); valid=false; }else{ $('#pemail').attr('style', ''); }
    if(!validatePhone(phone))
    { $('#purphone').attr('style', 'border-color:red !important'); valid=false; }else{ $('#purphone').attr('style', ''); }
    if($.trim(message) == '')
    { $('#pmessage').attr('style', 'border-color:red !important'); valid=false; }else{ $('#pmessage').attr('style', ''); }
    if(!robot)
    {
     $('.robot-error').text('(Please verify you are not robot.)');
    }else if(valid == false){

    }else{
      	$(thiss).attr('disabled', true);
      	$(thiss).find('.fa-spin').show();
      	$.ajax({
      		url: site_url+'developer_mng/contact',
      		type: 'POST',
      		dataType: 'json',
      		data: {q: p_id, name:name, email:email, phone:phone, message:message},
      		success:function(data)
      		{
      			//alert(data);
      			$(thiss).attr('disabled', false);
      			$(thiss).find('.fa-spin').hide();
      			if(data['error'] == 1)
      			{
      				if(data['q'] != '')
      				{ $('.lead_fail').slideDown();}else{$('.lead_fail').slideUp();}
      				if(data['name'] != '')
				    { $('#pname').attr('style', 'border-color:red !important'); valid=false;  }else{ $('#pname').attr('style', ''); }
				    if(data['email'] != '')
				    { $('#pemail').attr('style', 'border-color:red !important'); valid=false; }else{ $('#pemail').attr('style', ''); }
				    if(data['phone'] != '')
				    { $('#purphone').attr('style', 'border-color:red !important'); valid=false; }else{ $('#purphone').attr('style', ''); }
				    if(data['message'] != '')
				    { $('#pmessage').attr('style', 'border-color:red !important'); valid=false; }else{ $('#pmessage').attr('style', ''); }
      	
      			}else if(data['error'] == 2){
      				$('.lead_dublicate').slideDown();
      				setTimeout(function(){ $('.lead_dublicate').slideUp();},3000);
      			}else{
      				$('.lead_success').slideDown();
      				setTimeout(function(){ $('.lead_success').slideUp();},3000);
      				setTimeout(function(){ $('#emailModal').modal('hide')},3500);
      			}

      		}
      	})
      	.fail(function() {
      		$(thiss).attr('disabled', false);
      		$(thiss).find('.fa-spin').hide();
      		$('.lead_fail').slideDown();
      		setTimeout(function(){ $('.lead_fail').slideUp();},3000);
      		console.log("error");
      	});
      		
    }
});

